"use client"

import type { Board, Player } from "@/types/game"

interface GameBoardProps {
  board: Board
  winningLine: number[] | null
  isGameActive: boolean
  isAiThinking: boolean
  currentPlayer: Player
  canMakeMove: boolean
  onCellClick: (index: number) => void
}

export function GameBoard({
  board,
  winningLine,
  isGameActive,
  isAiThinking,
  currentPlayer,
  canMakeMove,
  onCellClick,
}: GameBoardProps) {
  return (
    <div className="relative">
      <div className="grid grid-cols-3 gap-1 bg-border p-1 rounded-md select-none">
        {board.map((cell, index) => (
          <button
            key={index}
            className={`aspect-square bg-background flex items-center justify-center text-4xl font-extralight transition-all duration-200 hover:bg-muted disabled:cursor-not-allowed focus:outline-none relative group ${
              winningLine?.includes(index) ? "bg-muted" : ""
            }`}
            onClick={() => onCellClick(index)}
            disabled={!isGameActive || isAiThinking || !canMakeMove}
          >
            {cell && (
              <span
                className={`${
                  cell === "X" ? "text-foreground" : "text-muted-foreground"
                } transition-all duration-300 animate-in zoom-in-50`}
              >
                {cell}
              </span>
            )}
            {!cell && !isGameActive === false && isGameActive && !isAiThinking && canMakeMove && (
              <span className="absolute inset-0 flex items-center justify-center text-muted-foreground text-4xl font-extralight opacity-0 group-hover:opacity-100 transition-opacity duration-200">
                {currentPlayer}
              </span>
            )}
          </button>
        ))}
      </div>
    </div>
  )
}

"use client"

import { ArrowLeft, RotateCcw, Wifi, WifiOff } from "lucide-react"
import { GameBoard } from "./game-board"
import type { GameMode, GameState, OnlineState } from "@/types/game"

interface GameScreenProps {
  gameMode: GameMode
  gameState: GameState
  onlineState: OnlineState
  onBack: () => void
  onReset: () => void
  onCellClick: (index: number) => void
}

export function GameScreen({ gameMode, gameState, onlineState, onBack, onReset, onCellClick }: GameScreenProps) {
  const { board, currentPlayer, winner, isGameActive, winningLine, isAiThinking } = gameState
  const { room, playerSymbol, connectionStatus, roomCode } = onlineState

  const getOpponentName = () => {
    if (!room) return ""
    return room.player1_id === onlineState.playerId ? room.player2_name : room.player1_name
  }

  const getGameTitle = () => {
    switch (gameMode) {
      case "single":
        return "vs AI"
      case "local":
        return "LOCAL"
      case "online":
        return `vs ${getOpponentName()}`
      default:
        return ""
    }
  }

  const getGameStatus = () => {
    if (winner === "tie") {
      return "DRAW"
    }
    if (winner) {
      switch (gameMode) {
        case "single":
          return winner === "X" ? "YOU WIN" : "AI WINS"
        case "online":
          return winner === playerSymbol ? "YOU WIN" : "YOU LOSE"
        default:
          return `${winner} WINS`
      }
    }

    if (isAiThinking) {
      return <span className="animate-pulse">AI THINKING...</span>
    }

    switch (gameMode) {
      case "single":
        return currentPlayer === "X" ? "YOUR TURN" : "AI TURN"
      case "online":
        return currentPlayer === playerSymbol ? "YOUR TURN" : "OPPONENT'S TURN"
      default:
        return `${currentPlayer} TURN`
    }
  }

  const canMakeMove = () => {
    if (gameMode === "online") {
      return currentPlayer === playerSymbol
    }
    return true
  }

  return (
    <div className="min-h-screen bg-background flex items-center justify-center px-4 py-8 sm:p-8">
      <div className="w-full max-w-xs space-y-8 animate-in fade-in duration-500">
        {/* Header */}
        <div className="flex items-center justify-between select-none">
          <button onClick={onBack} className="p-2 hover:bg-muted/5 rounded-full transition-all duration-200 group">
            <ArrowLeft className="h-5 w-5 text-muted-foreground group-hover:text-foreground transition-colors" />
          </button>
          <div className="text-center">
            <h2 className="text-sm font-light tracking-widest text-muted-foreground uppercase">{getGameTitle()}</h2>
            {gameMode === "online" && (
              <div className="flex items-center justify-center space-x-1 mt-1">
                {connectionStatus === "connected" ? (
                  <Wifi className="h-3 w-3 text-success" />
                ) : (
                  <WifiOff className="h-3 w-3 text-error" />
                )}
                <span className="text-xs text-muted-foreground/40">
                  {playerSymbol} • {roomCode}
                </span>
              </div>
            )}
          </div>
          <button onClick={onReset} className="p-2 hover:bg-muted/5 rounded-full transition-all duration-200 group">
            <RotateCcw className="h-5 w-5 text-muted-foreground group-hover:text-foreground transition-colors" />
          </button>
        </div>

        {/* Game Status */}
        <div className="text-center h-8 flex items-center justify-center select-none">
          <div className="text-muted-foreground/40 font-light tracking-wide">{getGameStatus()}</div>
        </div>

        {/* Game Board */}
        <GameBoard
          board={board}
          winningLine={winningLine}
          isGameActive={isGameActive}
          isAiThinking={isAiThinking}
          currentPlayer={currentPlayer}
          canMakeMove={canMakeMove()}
          onCellClick={onCellClick}
        />

        {/* Action Buttons */}
        {winner && (
          <div className="space-y-3 animate-in slide-in-from-bottom-4 duration-500">
            <button
              onClick={onReset}
              className="w-full py-3 bg-foreground text-background font-light tracking-wide hover:bg-foreground/80 transition-all duration-200"
            >
              PLAY AGAIN
            </button>
            <button
              onClick={onBack}
              className="w-full py-3 text-foreground font-light tracking-wide hover:bg-muted/5 transition-all duration-200 border border-foreground"
            >
              MENU
            </button>
          </div>
        )}
      </div>
    </div>
  )
}

"use client"
import { ArrowLeft } from "lucide-react"
import type { OnlineMode, ConnectionStatus } from "@/types/game"

interface OnlineSetupScreenProps {
  onlineMode: OnlineMode
  setOnlineMode: (mode: OnlineMode) => void
  playerName: string
  setPlayerName: (name: string) => void
  joinCode: string
  setJoinCode: (code: string) => void
  connectionStatus: ConnectionStatus
  onCreateRoom: () => void
  onJoinRoom: () => void
  onBack: () => void
}

export function OnlineSetupScreen({
  onlineMode,
  setOnlineMode,
  playerName,
  setPlayerName,
  joinCode,
  setJoinCode,
  connectionStatus,
  onCreateRoom,
  onJoinRoom,
  onBack,
}: OnlineSetupScreenProps) {
  return (
    <div className="min-h-screen bg-background flex items-center justify-center px-4 py-8 sm:p-8">
      <div className="w-full max-w-xs space-y-8 animate-in fade-in duration-500">
        <div className="flex items-center justify-between">
          <button onClick={onBack} className="p-2 hover:bg-muted/5 rounded-full transition-all duration-200 group">
            <ArrowLeft className="h-5 w-5 text-muted-foreground group-hover:text-foreground transition-colors" />
          </button>
          <h2 className="text-sm font-light tracking-widest text-muted-foreground uppercase">ONLINE</h2>
          <div className="w-9"></div>
        </div>

        <div className="space-y-6">
          {/* Mode Toggle - Ultra Minimal */}
          <div className="flex">
            <button
              onClick={() => setOnlineMode("create")}
              className={`flex-1 py-2 text-sm font-light tracking-wider transition-all duration-200 relative ${
                onlineMode === "create"
                  ? "text-foreground"
                  : "text-muted-foreground/30 hover:text-muted-foreground/50"
              }`}
            >
              CREATE
              {onlineMode === "create" && (
                <div className="absolute -bottom-1 left-1/2 w-4 h-[2px] bg-muted-foreground/40 transform -translate-x-1/2 transition-all duration-200" />
              )}
            </button>
            <button
              onClick={() => setOnlineMode("join")}
              className={`flex-1 py-2 text-sm font-light tracking-wider transition-all duration-200 relative ${
                onlineMode === "join"
                  ? "text-foreground"
                  : "text-muted-foreground/30 hover:text-muted-foreground/50"
              }`}
            >
              JOIN
              {onlineMode === "join" && (
                <div className="absolute -bottom-1 left-1/2 w-4 h-[2px] bg-muted-foreground/40 transform -translate-x-1/2 transition-all duration-200" />
              )}
            </button>
          </div>

          <div className="space-y-4">
            <input
              type="text"
              placeholder="YOUR NAME"
              value={playerName}
              onChange={(e) => setPlayerName(e.target.value)}
              className="w-full py-3 px-0 bg-transparent border border-border focus:border-foreground outline-none font-light tracking-wide placeholder:text-muted-foreground/40 text-center"
              maxLength={20}
            />

            {onlineMode === "join" && (
              <input
                type="text"
                placeholder="ROOM CODE"
                value={joinCode}
                onChange={(e) => setJoinCode(e.target.value.toUpperCase())}
                className="w-full py-3 px-0 bg-transparent border border-border focus:border-foreground outline-none font-light tracking-widest placeholder:text-muted-foreground/40 text-center"
                maxLength={6}
              />
            )}
          </div>

          <button
            onClick={onlineMode === "create" ? onCreateRoom : onJoinRoom}
            disabled={
              !playerName.trim() || (onlineMode === "join" && !joinCode.trim()) || connectionStatus === "connecting"
            }
            className="w-full py-4 bg-foreground text-background font-light tracking-wide hover:bg-foreground/80 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {connectionStatus === "connecting"
              ? onlineMode === "create"
                ? "CREATING..."
                : "JOINING..."
              : onlineMode === "create"
                ? "CREATE ROOM"
                : "JOIN ROOM"}
          </button>
        </div>
      </div>
    </div>
  )
}

