import { supabase } from "@/lib/supabase"
import type { Room, Board, Player } from "@/types/game"
import { generateRoomCode, checkWinner } from "@/utils/game-logic"
import type { RealtimeChannel } from "@supabase/supabase-js"

export class OnlineGameService {
  private channel: RealtimeChannel | null = null

  async createRoom(playerId: string, playerName: string): Promise<Room> {
    const code = generateRoomCode()

    const { data, error } = await supabase
      .from("rooms")
      .insert({
        id: code,
        player1_id: playerId,
        player1_name: playerName.trim(),
        board: Array(9).fill(null),
        status: "waiting",
        current_player: "X",
      })
      .select()
      .single()

    if (error) throw error
    return data
  }

  async joinRoom(roomId: string, playerId: string, playerName: string): Promise<Room> {
    const { data: existingRoom, error: fetchError } = await supabase
      .from("rooms")
      .select("*")
      .eq("id", roomId.toUpperCase())
      .single()

    if (fetchError || !existingRoom) {
      throw new Error("Room not found!")
    }

    if (existingRoom.player2_id) {
      throw new Error("Room is full!")
    }

    const { data, error } = await supabase
      .from("rooms")
      .update({
        player2_id: playerId,
        player2_name: playerName.trim(),
        status: "playing",
      })
      .eq("id", roomId.toUpperCase())
      .select()
      .single()

    if (error) throw error
    return data
  }

  async makeMove(roomId: string, board: Board, playerSymbol: Player): Promise<void> {
    const gameResult = checkWinner(board)
    const nextPlayer = playerSymbol === "X" ? "O" : "X"

    // Convert Board (Player[]) to string[] for database storage
    const boardForDb = board.map(cell => cell === null ? null : cell)

    await supabase
      .from("rooms")
      .update({
        board: boardForDb,
        current_player: gameResult.winner ? playerSymbol : nextPlayer,
        winner: gameResult.winner || null,
        status: gameResult.winner ? "finished" : "playing",
      })
      .eq("id", roomId)
  }

  async resetGame(roomId: string): Promise<void> {
    const { data: room } = await supabase
      .from("rooms")
      .select("current_player")
      .eq("id", roomId)
      .single()

    if (!room) return

    await supabase
      .from("rooms")
      .update({
        board: Array(9).fill(null),
        current_player: room.current_player === "X" ? "O" : "X",
        winner: null,
        status: "playing",
      })
      .eq("id", roomId)
  }

  subscribeToRoom(
    roomId: string,
    onUpdate: (room: Room) => void,
    onStatusChange: (status: string) => void,
  ): RealtimeChannel {
    this.channel = supabase
      .channel(`room:${roomId}`)
      .on(
        "postgres_changes",
        {
          event: "UPDATE", // Listen specifically to updates
          schema: "public",
          table: "rooms",
          filter: `id=eq.${roomId}`,
        },
        (payload) => {
          console.log("Room update received:", payload.new) // Debug log
          const updatedRoom = payload.new as Room
          onUpdate(updatedRoom)
        },
      )
      .on(
        "postgres_changes",
        {
          event: "INSERT", // Also listen to inserts (just in case)
          schema: "public",
          table: "rooms",
          filter: `id=eq.${roomId}`,
        },
        (payload) => {
          console.log("Room insert received:", payload.new) // Debug log
          const updatedRoom = payload.new as Room
          onUpdate(updatedRoom)
        },
      )
      .subscribe((status) => {
        console.log("Subscription status:", status) // Debug log
        onStatusChange(status)
      })

    return this.channel
  }

  unsubscribe(): void {
    if (this.channel) {
      this.channel.unsubscribe()
      this.channel = null
    }
  }
}

import type { Board, Player } from "@/types/game"
import { WINNING_COMBINATIONS } from "@/constants/game"

export const checkWinner = (board: Board): { winner: Player | "tie" | null; line: number[] | null } => {
  for (const combination of WINNING_COMBINATIONS) {
    const [a, b, c] = combination
    if (board[a] && board[a] === board[b] && board[a] === board[c]) {
      return { winner: board[a], line: combination }
    }
  }

  if (board.every((cell) => cell !== null)) {
    return { winner: "tie", line: null }
  }

  return { winner: null, line: null }
}

export const makeAIMove = (board: Board): number => {
  // Check if AI can win
  for (let i = 0; i < 9; i++) {
    if (board[i] === null) {
      const testBoard = [...board]
      testBoard[i] = "O"
      if (checkWinner(testBoard).winner === "O") {
        return i
      }
    }
  }

  // Check if AI needs to block player
  for (let i = 0; i < 9; i++) {
    if (board[i] === null) {
      const testBoard = [...board]
      testBoard[i] = "X"
      if (checkWinner(testBoard).winner === "X") {
        return i
      }
    }
  }

  // Take center if available
  if (board[4] === null) return 4

  // Take corners
  const corners = [0, 2, 6, 8]
  const availableCorners = corners.filter((i) => board[i] === null)
  if (availableCorners.length > 0) {
    return availableCorners[Math.floor(Math.random() * availableCorners.length)]
  }

  // Take any available space
  const availableSpaces = board.map((cell, index) => (cell === null ? index : null)).filter((val) => val !== null)
  return availableSpaces[Math.floor(Math.random() * availableSpaces.length)] as number
}

export const generateRoomCode = (): string => {
  const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789"
  let result = ""
  for (let i = 0; i < 6; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length))
  }
  return result
}

-- Create rooms table
CREATE TABLE IF NOT EXISTS rooms (
  id VARCHAR(6) PRIMARY KEY,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  player1_id UUID,
  player2_id UUID,
  player1_name VARCHAR(50),
  player2_name VARCHAR(50),
  current_player VARCHAR(1) DEFAULT 'X' CHECK (current_player IN ('X', 'O')),
  board TEXT[] DEFAULT ARRAY[NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL],
  winner VARCHAR(10),
  status VARCHAR(20) DEFAULT 'waiting' CHECK (status IN ('waiting', 'playing', 'finished'))
);

-- Enable Row Level Security
ALTER TABLE rooms ENABLE ROW LEVEL SECURITY;

-- Create policies for rooms
CREATE POLICY "Anyone can read rooms" ON rooms FOR SELECT USING (true);
CREATE POLICY "Anyone can create rooms" ON rooms FOR INSERT WITH CHECK (true);
CREATE POLICY "Players can update their rooms" ON rooms FOR UPDATE USING (
  auth.uid() = player1_id OR auth.uid() = player2_id OR auth.uid() IS NULL
);

-- Create function to generate room codes
CREATE OR REPLACE FUNCTION generate_room_code()
RETURNS TEXT AS $$
DECLARE
  chars TEXT := 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  result TEXT := '';
  i INTEGER;
BEGIN
  FOR i IN 1..6 LOOP
    result := result || substr(chars, floor(random() * length(chars) + 1)::INTEGER, 1);
  END LOOP;
  RETURN result;
END;
$$ LANGUAGE plpgsql;

-- Create function to clean up old rooms (optional)
CREATE OR REPLACE FUNCTION cleanup_old_rooms()
RETURNS void AS $$
BEGIN
  DELETE FROM rooms 
  WHERE created_at < NOW() - INTERVAL '24 hours';
END;
$$ LANGUAGE plpgsql;
